INSERT INTO `wp_posts` VALUES (2, 1, '2008-07-09 23:49:42', '2008-07-10 03:49:42', '<p style="text-justify: inter-ideograph; text-align: justify;"><span style="font-size: 10pt; font-family: Arial;"><strong>About <em>getchee</em></strong></span></p>\r\n<p style="text-justify: inter-ideograph; text-align: justify;"><span style="font-size: 10pt; font-family: Arial;"><em>getchee</em> develops on-demand, Web-native, applications and services designed to help people access and manage their location intelligence in the world’s fastest growing markets. The idea for <em>getchee</em> was founded by global consulting firm Pacific GeoPro, now GeoPro. After more than 10 years of experience in emerging economies, the founders at Pacific GeoPro were able to harness all their knowledge of poor market data and standardized it into a high-value, on-demand innovation called <em>getchee</em>. <em>getchee</em> is a tool for both the technical and non-technical business users.</span></p>\r\n\r\n<p style="text-justify: inter-ideograph; text-align: justify;">\r\n<div style="text-justify: inter-ideograph; text-align: justify;"><span style="font-size: 10pt; font-family: Arial;"><span style="font-size: 10pt; font-family: Arial;"><em>getchee</em> is a privately owned company with offices in Taipei, Taiwan and Shanghai, China. For information on our products and services, please contact us at: <a href="mailto:info@getchee.com"><span style="color: #800080;">info@getchee.com</span></a>, or give us a call at +886.2.7718.0700.</span></span></div>\r\n<div style="text-justify: inter-ideograph; text-align: justify;"><span style="font-size: 10pt; font-family: Arial;"> </span></div>\r\n<div style="text-justify: inter-ideograph; text-align: justify;"><span style="font-size: 10pt; font-family: Arial;"><span style="font-size: 10pt; font-family: Arial;"><span style="font-size: 10pt; font-family: Arial;"></span></span></span><span style="font-size: 10pt; font-family: Arial;">Visit our Website at <a href="http://www.getchee.com" target="_blank">www.getchee.com</a></span>\r\n\r\n </div>', 'About', 0, '', 'trash', 'open', 'open', '', 'about', '', '', '2013-02-21 01:28:41', '2013-02-21 05:28:41', '', 0, 'http://blog.getchee.com/?page_id=2', 0, 'page', '', 0, ''); 
