INSERT INTO `wp_posts` VALUES (3609, 9, '2012-06-12 21:56:43', '2012-06-13 01:56:43', '<iframe src="http://player.vimeo.com/video/43595421" frameborder="0" width="526" height="281"></iframe>\r\n<div>\r\n<div data-expand-tooltip="Click to expand description">\r\n\r\ngetchee creates and validates market data for business use throughout China, India, and Southeast Asia.\r\n\r\nThrough the use of GIS, we help companies compare and visualize business data against our market data on a map. We maintain a database that supports over 100 cities and three types of data: demographics, points of interest, and heat maps.\r\n\r\nWe make this market insight accessible through <a title="Demographic and business point data for use in China, India, and Southeast Asia" href="http://www.getchee.com/data/data-overview.html?utm_source=getchee&amp;utm_medium=blog&amp;utm_campaign=new%2Bcompany%2Bvideo" target="_blank">raw data</a> acquisitions, <a title="Consulting services to help brand owners make intelligent business decisions in Asia" href="http://www.getchee.com/services/services-overview.html?utm_source=getchee&amp;utm_medium=blog&amp;utm_campaign=new%2Bcompany%2Bvideo" target="_blank">consulting services</a>, and <a title="Visualize business and market data on a map for better validation, communication, and discovery" href="http://www.getchee.com/products/kiwi-overview.html?utm_source=getchee&amp;utm_medium=blog&amp;utm_campaign=new%2Bcompany%2Bvideo" target="_blank">software tools</a>. We are committed to making these outputs valuable to<a title="Some of the folks we work with" href="http://www.getchee.com/about/our-clients.html?utm_source=getchee&amp;utm_medium=blog&amp;utm_campaign=new%2Bcompany%2Bvideo" target="_blank"> our clients</a>.\r\n\r\n</div>\r\n</div>', 'Improving Data Quality in Asia', 0, '', 'publish', 'open', 'open', '', 'improving-data-quality-in-asia', '', '', '2012-07-31 22:49:30', '2012-08-01 02:49:30', '', 0, 'http://blog.getchee.com/?p=3609', 0, 'post', '', 0,