INSERT INTO `wp_posts` VALUES (3570, 12, '2012-05-29 23:40:03', '2012-05-29 15:40:03', '<p>Where are the wealthiest places in China and the most expensive places to live? Who are the wealthiest people in China, and where are they spending their money and what Chinese luxury brands are they buying? This infographic takes a look.</p>\r\n<p><!--more--></p>\r\n<h3>Stay updated. <a title="Stay ahead of the curve in Asia with getchee\'s newsletter." href="http://www.getchee.com/newsletter/index.html?utm_source=Getchee+Blog&utm_medium=In+Post+Link&utm_content=Bloom+Post+Mar+29+2012&utm_campaign=Newsletter+Subscribe" target="_blank">Subscribe</a> now to receive valuable insights each week from the web\'s best blog on retail growth in Asia.</h3> ', 'infographic - Wealth in China', 0, '', 'publish', 'open', 'open', '', 'wealth-in-china', '', '', '2013-09-14 16:04:08', '2013-09-14 08:04:08', '', 0, 'http://blog.getchee.com/?p=3570', 0, 'post', '', 1, '4427'); 
